html { overflow: scroll; overflow-x: hidden; }


#kard {
    padding: 0px 0px;
   %max-width: 700px;
    margin: 0 auto; /* CENTERS THE CARD IN THE MIDDLE OF THE WINDOW */
    word-wrap: break-word; /* BREAKS UP LONG WORKS */
}


.card {
 font-family: latin modern roman;
 font-size: 20px;
 text-align: center;
 color: #D7DEE9;
 background-color: #333B45;
}


/* STYLE FOR CLOZE DELETIONS */
.cloze, .cloze b, .cloze u, .cloze i { font-weight: bold; color: IndianRed !important ;}


/* STYLE FOR EXTRA PORTION ON BACK OF CARD */
#extra, #extra i { font-size: 18px; color:#D7DEE9; font-style: italic; }



/* COLOR ACCENTS FOR BOLD-ITALICS-UNDERLINE */
b { color: MediumSeaGreen !important; } /* BOLD STYLE */
u { text-decoration: none ; color: #FAB763; } /* UNDERLINE STYLE */  
i  { font-style: normal; color: #5EB3B3; } /* ITALICS STYLE */
a { color: LightGray !important; text-decoration: none; font-size: 10px; font-style: normal; } /* LINK STYLE */


.mystyle1 {
color: #4F5B66;
font-size: 13px;
position: relative;
            }
.mystyle1:hover {
	color: #C695C6;
	cursor: default;
	}


.mystyle2 {
color: #d4cfc9;
font-size: 13px;
margin: auto;
border: 2px solid #6898D0;
padding: 5px;
width: 100%;
max-width: 700px;
text-align: left;
            }

img { width: auto; height: auto; max-width: None; max-height: 300}
img.mobile { max-width: 100%; max-height: 100%; }


.button {
  outline: 0;
  background-color: #333B45; 
  border-radius: 0.12em;
  border: 1px solid #4F5B66;
  color: #6898D0;
